import React from "react";

export const Sidebar = ({ tasks, setTasks }) => {
  const colors = ["#5C0308", "#BF1C24", "#145444", "#A4713E", "#F1BA86"];

  const randomColor = colors[Math.floor(Math.random() * colors.length)];

  const cardStyle = {
    backgroundColor: randomColor,
  };

  return (
    <div className="bg-gray-100 min-h-screen">
      <h1 className="flex justify-center text-4xl py-3 font-bold tracking-wider text-blue-900 mb-10 pt-5">
        Weekly
      </h1>
      <div>
        <div className="flex flex-col">
          {tasks.map((task) => (
            <div
              key={task.id}
              className="border-2 mx-5 border-black pl-3 py-2 rounded-full"
            >
              <span className="font-bold text-gray-500">{task.text}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
