import React from "react";
import "./Task.scss";
import { ItemTypes } from "../../util/types";
import { useDrag } from "react-dnd";
import { useTasks } from "../../Hooks/hooks";

export const Task = (props) => {
  const { id, tag } = props.task;

  const { removeTask } = useTasks();

  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.TASK,
    item: {
      id: id,
      text: props.task.text,
      tag: tag,
    },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div
      style={{ backgroundColor: props.task.color }}
      className={"task " + (isDragging ? " selected" : "")}
      ref={drag}
    >
      <span className="font-bold text-gray-600 cursor-default">
        {props.task.text}
      </span>
      <button className="focus:outline-none" onClick={() => removeTask(id)}>
        <i className="fas fa-times delete-button cursor-pointer"></i>
      </button>
    </div>
  );
};
