import React from "react";
import "./Day.scss";
import { useDrop } from "react-dnd";
import { ItemTypes } from "../../util/types";

export const Day = ({ name, dayIndex, tasks, setTasks }) => {
  const changeTicketTag = (id, newTag) => {
    const task = tasks.find((task) => task.id === id);
    task.tag = newTag;
    setTasks(task.filter((task) => task.id !== id).concat(task));
  };

  const [{ isOver }, drop] = useDrop({
    accept: ItemTypes.Task,
    drop: (task) => changeTicketTag(task.id, newTag),
  });

  const isActiveDay = () => {
    return new Date().getDay() === dayIndex;
  };

  return (
    <div>
      <h1 className={isActiveDay() ? "active-day" : "day-color"}>{name}</h1>
      <div className="day-background"></div>
    </div>
  );
};
