import "../../App.css";
import "./App.scss";
import { Sidebar } from "../Sidebar/Sidebar";
import { Header } from "../Header/Header";
import { Calendar } from "../Calendar/Calendar";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import { useTasks } from "../../Hooks/hooks";
import { v4 as uuidv4 } from "uuid";
import { randomColor } from "../util/randomColor";

function App() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const storedTasks = localStorage.getItem("tasks");
    if (storedTasks) setTasks(JSON.parse(storedTasks));
  }, []);

  useEffect(() => {
    persistTasksToStorage(tasks);
  }, [tasks]);

  //Functions

  const addNewTask = (newTaskInput) => {
    setTasks(
      tasks.concat({
        id: uuidv4(),
        tag: "QUEUE",
        text: newTaskInput,
        color: randomColor(),
      })
    );
  };

  const changeTaskTag = (id, newTag) => {
    const task = tasks.find((task) => task.id === id);
    task.tag = newTag;
    setTasks(tasks.filter((task) => task.id !== id).concat(task));
  };

  const removeTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const removeAllTasks = () => {
    setTasks([]);
  };

  const persistTasksToStorage = (tasks) => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="App text-gray-600 overflow-hidden min-h-screen">
        <div className="container">
          <Sidebar
            tasks={tasks}
            changeTaskTag={changeTaskTag}
            removeTask={removeTask}
            setTasks={setTasks}
          />
          <main className="calendar-header">
            <Header addNewTask={addNewTask} removeAllTasks={removeAllTasks} />
            <Calendar
              tasks={tasks}
              removeTask={removeTask}
              changeTaskTag={changeTaskTag}
              setTasks={setTasks}
            />
          </main>
        </div>
      </div>
    </DndProvider>
  );
}

export default App;
