import React, { useState } from "react";
import "./Task.scss";
import { ItemTypes } from "../../util/types";
import { useDrag } from "react-dnd";

export const Task = (props) => {
  const { id, tag, theme } = props.ticket;

  const [isEditMode, setIsEditMode] = useState(false);
  const [description, setDescription] = useState(props.ticket.description);

  const [{ isDragging }, drag] = useDrag({
    item: {
      type: ItemTypes.TICKET,
      id: id,
      description: props.ticket.description,
      tag: tag,
    },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
    canDrag: () => !isEditMode,
  });

  const ticketStyle = {
    backgroundColor: `var(--color-${theme}-background)`,
    color: `var(--color-${theme}-text)`,
  };

  const deleteButtonStyle = {
    color: `var(--color-${theme}-text)`,
  };

  const inputStyle = {
    color: `var(--color-${theme}-text)`,
    border: `2px solid var(--color-${theme}-text)`,
  };

  function updateDescription() {
    setIsEditMode(false);
    props.updateDescription(id, description);
  }

  return (
    <div
      style={{ backgroundColor: props.task.color }}
      className={"task " + (isDragging ? " selected" : "")}
      ref={drag}
    >
