import React from "react";
import "./Task.scss";
import { ItemTypes } from "../../util/types";
import { useDrag, useDrop } from "react-dnd";

export const Task = ({ task, tasks, setTasks }) => {
  const removeTask = (id) => {
    console.log(id);
    let newTasks = tasks.filter((task) => task.id !== id);
    setTasks(newTasks);
  };

  const [{ isDragging }, drag] = useDrag({
    task: {
      type: ItemTypes.task,
      id: task.id,
      text: task.text,
    },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  return (
    <div
      style={{ backgroundColor: task.color }}
      key={task.id}
      className={"task " + (isDragging ? " selected" : "")}
      ref={drag}
    >
      <span className="font-bold text-gray-600 cursor-default">
        {task.text}
      </span>
      <i
        className="fas fa-times delete-button cursor-pointer"
        onClick={() => removeTask(task.id)}
      ></i>
    </div>
  );
};
