import React from "react";

export const Sidebar = ({ tasks, setTasks }) => {
  return (
    <div className="bg-gray-100 min-h-screen">
      <h1 className="flex justify-center text-4xl py-3 font-bold tracking-wider text-blue-900">
        Weekly
      </h1>
      <div>
        <div className="flex flex-col">
          {tasks.map((task) => (
            <div key={task.id} className="border-2 mx-5 border-black pl-3 py-2">
              <span>{task.text}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
