import React from "react";
import "./EditableDescription.scss";

export const EditableText = (props) => {
  const { text, onChange, onSubmit, isEditMode, style } = props;

  if (isEditMode) {
    return (
      <form
        onSubmit={(e) => {
          e.preventDefault();
          onSubmit();
        }}
      >
        <input
          className="editable-description-input"
          type="text"
          value={text}
          onChange={onChange}
          style={style}
        />
      </form>
    );
  }

  return (
    <p className="font-bold text-gray-600 cursor-default">{props.task.text}</p>
  );
};
